definition.script:
pipeline {
            agent any

            stages {
            stage('Clone Repository') {
            steps {
            checkout([$class: 'GitSCM',
            branches: [[name: 'refs/heads/AR-6-Create-docker-compose-for-all-elements']],
            doGenerateSubmoduleConfigurations: false,
            extensions: [],
            submoduleCfg: [],
            userRemoteConfigs: [[url: 'https://github.com/buatss/ArticleTracker']]])
            }
            }

            stage('Print Java and Maven Versions') {
            steps {
            script {
            def javaVersion = sh(script: 'java -version', returnStdout: true).trim()
            def mavenVersion = sh(script: 'mvn -version', returnStdout: true).trim()
            echo "Java Version: ${javaVersion}"
            echo "Maven Version: ${mavenVersion}"
            }
            }
            }

            stage('Build Project') {
            steps {
            sh 'chmod +x mvnw'
            sh './mvnw clean install surefire-report:report spring-boot:run -Dspring-boot.run.profiles=dev'
            }
            }
            }
            }
